var flies=[];function randRange(b,a){var c=Math.floor(Math.random()*(a-b+1))+b;return c}function Fly(a,b){this.x=a;this.y=b;this.ctx=jQuery("#canvas")[0].getContext("2d");this.angle=-1.55;this.a=0;num=randRange(0,10);if(num<5){this.img=img}else{this.img=img2}}Fly.prototype.draw=function(){this.a+=(Math.random()*0.1)-0.05;this.a*=0.9;this.angle+=this.a;var a=Math.sin(this.angle)*2;var b=Math.cos(this.angle)*5;this.x+=b;this.y+=a;this.ctx.drawImage(this.img,this.x,this.y)};Fly.prototype.init=function(){d=0;d++};var img=new Image();var img2=new Image();img.onload=function(){};img.src="img/particle2.png";img2.src="img/particle3.png";function drawFlies(){jQuery("#canvas")[0].getContext("2d").clearRect(0,0,480,320);deleted=0;for(var a in flies){fly=flies[a];fly.draw();if((fly.x>480)||(fly.x<(-5*3))||(fly.y>320)||(fly.y<-5)){toto=flies.splice(a,1);deleted++;delete toto}}for(var a=0;a<deleted;a++){flies.push(new Fly(randRange(0,480),randRange(50,300)))}};