var TimerManager={time:{h:0,m:0,s:0},timeInterval:null,callback:null,init:function(a){this.callback=a},start:function(a){this.time={h:a.h,m:a.m,s:a.s};this.timeInterval=setInterval(jQuery.proxy(TimerManager.setTime,TimerManager),1000)},stop:function(){if(this.timeInterval===null){Console.log("TimerManager::stop() WARN: calling stop and manager already stopped !")}else{clearInterval(this.timeInterval);this.time={h:0,m:0,s:0}}},setTime:function(){this.time.s++;if(this.time.s===60){this.time.m++;this.time.s=0}if(this.time.m===60){this.time.h++;this.time.m=0}if(this.callback){this.callback(this.time)}}};